const Chart2 = () => {
  const [content, setContent] = useState({});
  const [loading, setLoading] = useState(true);
  var labels,data,arrChart;
  const options = {
    responsive: true,
    plugins: {
      title: {
        display: true,
        text: 'Chart.js Line Chart',
      },
    },
  };
	useEffect(() => {
    fetch("https://workers.cnainani.workers.dev/attack-layer3", { mode: "cors" })
    .then((response) => {
      console.log("response is:"+response);
      return response.json();
    })  
    .then((dataRes) => {
        let startTime=dataRes.data.meta.dateRange.startTime.split('T')[0]+'"';
        let endTime=dataRes.data.meta.dateRange.endTime.split('T')[0]+'"';
        arrChart=getDaysArray(startTime,endTime);
        labels = [arrChart];
        console.log("lables are:"+[arrChart]);
        data = {
          labels,
          datasets: [
            {
              label: 'Dataset 1',
              data: "500",
              borderColor: 'rgb(255, 99, 132)',
              backgroundColor: 'rgba(255, 99, 132, 0.5)',
            },
            {
              label: 'Dataset 2',
              data: "1000",
              borderColor: 'rgb(53, 162, 235)',
              backgroundColor: 'rgba(53, 162, 235, 0.5)',
            },
          ],
        };
        console.log("data to be set:"+data);
        setContent(data);
       // content=data;
        console.log("content is:"+JSON.stringify(data));
        setLoading(false);
        console.log("data inside is:"+JSON.stringify(data));
        //return <Line options={options} data={data} />;
    });
    var getDaysArray = function(start, end) {
        for(var arr=[],dt=new Date(start); 
          dt<=new Date(end); 
          dt.setDate(dt.getDate()+1)){
            console.log("date pushed is:"+ format(new Date(dt), 'MM/dd/yyyy'));
            arr.push(format(new Date(dt), 'MM/dd/yyyy'));
        }
        console.log("array is:"+arr);
        return arr;
    };
  },[]);

  if (loading) {
    console.log("loading screen");
    return <Container>loading screen</Container>;
  }
  return (
    <Container>
       <Line options={options} data={content} />
    </Container>
  );
};


 export default Chart2;
