import { useEffect, useState } from "react";
import { Container } from "react-bootstrap";
import { format } from 'date-fns'
import React from 'react';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';
import { Line } from 'react-chartjs-2';

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);


const TrafficChangeChart = () => {
  const [content, setContent] = useState({});
  const [loading, setLoading] = useState(true);
  var labels,data,arrChart;
  const options = {
    responsive: true,
    plugins: {
      title: {
        display: true,
        text: 'Chart.js Line Chart',
      },
    },
  };
	useEffect(() => {
    fetch("https://workers.cnainani.workers.dev/traffic-change", { mode: "cors" })
    .then((response) => {
      console.log("response is:"+response);
      return response.json();
    })  
    .then((dataRes) => {
      console.log("total data is:"+dataRes.data.total.values);
       // let startTime=dataRes.data.meta.dateRange.startTime.split('T')[0]+'"';
        //let endTime=dataRes.data.meta.dateRange.endTime.split('T')[0]+'"';
        //arrChart=getDaysArray(startTime,endTime);
        labels = dataRes.data.total.timestamps;
        const result = dataRes.data.total.values;
        const resultHttp = dataRes.data.http.values;
        console.log("result is:"+result);
       // console.log("lables are:"+arrChart.toString().split(','));
        data = {
          labels,
          datasets: [
            {
              label: 'Total',
              data: result,
              borderColor: 'rgb(255, 99, 132)',
              backgroundColor: 'rgba(255, 99, 132, 0.5)',
            },
            {
                label: 'Http',
                data: resultHttp,
                borderColor: 'rgb(53, 162, 235)',
                backgroundColor: 'rgba(53, 162, 235, 0.5)',
              }
          ],
        };
        console.log("data to be set:"+data);
        setContent(data);
        console.log("content is:"+JSON.stringify(data));
        setLoading(false);
    });
    var getDaysArray = function(start, end) {
        for(var arr=[],dt=new Date(start); 
          dt<=new Date(end); 
          dt.setDate(dt.getDate()+1)){
            console.log("date pushed is:"+ format(new Date(dt), 'MM/dd/yyyy'));
            arr.push(format(new Date(dt), 'MM/dd/yyyy'));
        }
        console.log("array is:"+arr);
        return arr;
    };
  },[]);

  if (loading) {
    console.log("loading screen");
    return <Container>loading screen</Container>;
  }
  return (
    <Container>
       <Line options={options} data={content} />
    </Container>
  );
};


 export default TrafficChangeChart;
